interface ISuggestions {
    label: string;
    kind: string;
    insertText: string;
    detail?: string;
}

export interface IEditModelState {
    // model: ITextModel,
    viewstate?: ICodeEditorViewState | null,
    standaloneEditorConstructionOptions?: IStandaloneEditorConstructionOptions,

    onViewStateChange?: (state: ICodeEditorViewState | null) => void
}



export const CodeEditor: React.FC<IEditModelState> = (props, context) => {
    const container = useRef<HTMLDivElement>(null);
    let editor: monaco.editor.IStandaloneCodeEditor;
    let autoCompletion: monaco.IDisposable;

    //自定义提示
    let customAutoCompletion: ISuggestions[] = [{
        label: "item11",
        insertText: "item text",
        kind: "6",
        detail: "TABLE.COLLUMN"
    }];

    useEffect(()=>{
        console.log("props.viewstate effect");
    },[props.viewstate]);

    useEffect(()=>{
        console.log("props.standaloneEditorConstructionOptions effect");
        if (container.current){
            if(!editor) editor = monaco.editor.create(container.current);
            if(props.standaloneEditorConstructionOptions?.model) {
                console.log("set model");
                editor.setModel(props.standaloneEditorConstructionOptions?.model)
                autoCompletion = registerAutoCompletionLanguage(props.standaloneEditorConstructionOptions?.model.getLanguageId(), customAutoCompletion);
                editor.focus();
                //让父级保存view状态
                props.onViewStateChange&&props.onViewStateChange(editor.saveViewState());
                //切换成父级传进来的view状态
                if(props.viewstate){
                    editor.restoreViewState(props.viewstate);
                    editor.setScrollPosition({
                        scrollTop: props.viewstate.viewState.scrollTop,
                        scrollLeft: props.viewstate.viewState.scrollLeft
                    });
                }
            }

            return () => {
                if (autoCompletion) autoCompletion.dispose();   //防止自动提示重复
                editor.dispose();
            };
        }
    },[props]);

    useEffect(() => {
        if (container.current) {
    //         console.log("didMount ==> "+props.standaloneEditorConstructionOptions?.language);
    //         //didMount/didUpdateMount
    //         editor = monaco.editor.create(container.current);
    //         if(props.standaloneEditorConstructionOptions?.model) {
    //             console.log("set model");
    //             editor.setModel(props.standaloneEditorConstructionOptions?.model)
    //             autoCompletion = registerAutoCompletionLanguage(props.standaloneEditorConstructionOptions?.model.getLanguageId(), customAutoCompletion);
    //             editor.focus();
    //         }
            return () => {
                //didUnmount
                if (autoCompletion) autoCompletion.dispose();   //防止自动提示重复
                editor.dispose();
            };
        }
    }, []);

    // let sqlstate: IModelState = {model: monaco.editor.createModel("select * from table", "mysql"), viewstate: null};
    // let redisstate: IModelState = {model: monaco.editor.createModel("INFO ", "redis"), viewstate: null};

    // function changeModel(type: number) {
    //     if (autoCompletion) autoCompletion.dispose();
    //     //保存状态
    //     if (currentModel) {
    //         currentModel.viewstate = editor.saveViewState();
    //     }
    //     //切换
    //     switch (type) {
    //         case 1:
    //             currentModel = sqlstate;
    //             editor.setModel(currentModel.model);
    //             autoCompletion = registerAutoCompletionLanguage("mysql", customAutoCompletion);
    //             editor.focus();
    //             break;
    //         default:
    //             currentModel = redisstate;
    //             editor.setModel(currentModel.model);
    //             autoCompletion = registerAutoCompletionLanguage("redis", customAutoCompletion);
    //             editor.focus();
    //     }
    //     //恢复状态
    //     if (currentModel.viewstate) {
    //         editor.restoreViewState(currentModel.viewstate);
    //         editor.setScrollPosition({
    //             scrollTop: currentModel.viewstate.viewState.scrollTop,
    //             scrollLeft: currentModel.viewstate.viewState.scrollLeft
    //         });
    //     }
    // }

    return <div style={{height: "calc(100%)", width: "calc(100%)"}}>
        <div className="Editor" ref={container} style={{height: "calc(90%)", width: "calc(100%)"}}></div>
    </div>;
};
