interface ISuggestions {
    label: string;
    kind: string;
    insertText: string;
    detail?: string;
}

interface IModelState {
    model: ITextModel,
    position?: IPosition,
}

export const CodeEditor: React.FC<IStandaloneEditorConstructionOptions> = (props, context) => {
    const container = useRef<HTMLDivElement>(null);
    let editor: monaco.editor.IStandaloneCodeEditor;
    let autoCompletion: monaco.IDisposable;
    let currentModel: IModelState;
    //自定义提示
    let customAutoCompletion: ISuggestions[] = [{
        label: "item11",
        insertText: "item text",
        kind: "6",
        detail: "TABLE.COLLUMN"
    }];

    useEffect(() => {
        if (container.current) {
            //didMount/didUpdateMount
            editor = monaco.editor.create(container.current, {...props});
            editor.onDidChangeCursorPosition((pos) => {
                console.log("sss")
            });
            return () => {
                //didUnmount
                if (autoCompletion) autoCompletion.dispose();   //防止自动提示重复
                editor.dispose();
            };
        }
    }, []);

    let sqlstate: IModelState = {model: monaco.editor.createModel("select * from table", "mysql")};
    let redisstate: IModelState = {model: monaco.editor.createModel("INFO ", "redis")};

    function changeModel(type: number) {
        if (autoCompletion) autoCompletion.dispose();
        if (currentModel) {
            currentModel.position = currentModel.model.console.log(currentModel.position);
        }
        switch (type) {
            case 1:
                currentModel = sqlstate;
                editor.setModel(currentModel.model);
                editor.setPosition({lineNumber: 1, column: 10});
                autoCompletion = registerAutoCompletionLanguage("mysql", customAutoCompletion);
                editor.focus();
                break;
            default:
                currentModel = redisstate;
                editor.setModel(currentModel.model);
                autoCompletion = registerAutoCompletionLanguage("redis", customAutoCompletion);
                editor.focus();
        }
    }

    return <div style={{height: "calc(100%)", width: "calc(100%)"}}>
        <Button onClick={() => changeModel(1)}>model sql</Button>
        <Button onClick={() => changeModel(0)}>model redis</Button>
        <div className="Editor" ref={container} style={{height: "calc(100%)", width: "calc(100%)"}}></div>
    </div>;
};
