export const CodeEditor: React.FC<IStandaloneEditorConstructionOptions> = (props, context) => {
    const container = useRef<HTMLDivElement>(null);
    const [editor, setEditor] = useState<IStandaloneCodeEditor>();
    // let editor: monaco.editor.IStandaloneCodeEditor;
    let autoCompletion: monaco.IDisposable | null;

    useEffect(() => {
        console.log("mount");
        if (container.current) {
            editor = monaco.editor.create(container.current, {...props});
            if(!autoCompletion){
                autoCompletion = registerAutoCompletionLanguage(props.language);
            }
            return () => {
                console.log("dispose");
                if (autoCompletion) autoCompletion.dispose();   //防止自动提示重复
                editor.dispose();
            };
        }
    }, []);

    return <div className="Editor" ref={container} style={{height: "100vh", width: "100vw"}}></div>;
};
