import React, {useRef, useEffect} from 'react';
import * as monaco from 'monaco-editor';
import { language } from 'monaco-editor/esm/vs/basic-languages/sql/sql'

window.MonacoEnvironment = {
    getWorker: function (_moduleId: any, label: string) {
        monaco.languages.getLanguages().map((value, index, array)=>{
            console.log(value)
        });
        switch (label) {
            case 'editorWorkerService':
                return new Worker(new URL('monaco-editor/esm/vs/editor/editor.worker', import.meta.url));
            case 'css':
            case 'less':
            case 'scss':
                return new Worker(new URL('monaco-editor/esm/vs/language/css/css.worker', import.meta.url));
            case 'handlebars':
            case 'html':
            case 'razor':
                return new Worker(
                    new URL('monaco-editor/esm/vs/language/html/html.worker', import.meta.url),
                );
            case 'json':
                return new Worker(
                    new URL('monaco-editor/esm/vs/language/json/json.worker', import.meta.url),
                );
            case 'javascript':
            case 'typescript':
                return new Worker(
                    new URL('monaco-editor/esm/vs/language/typescript/ts.worker', import.meta.url),
                );
            default:
                throw new Error(`Unknown label ${label}`);
        }
    }
};

export const BaseEditor: React.FC = () => {
    const container = useRef<HTMLDivElement>(null);
    let editor: monaco.editor.IStandaloneCodeEditor;
    useEffect(() => {
        if (container.current) {
            editor = monaco.editor.create(container.current, {
                value: ['function x() {', '\tconsole.log("Hello world!");', '}'].join('\n'),
                language: 'javascript'
            });
        }
        return () => {
            editor.dispose();
        };
    }, []);
    return <div className="Editor" ref={container} style={{height: "100vh", width: "100vw"}}></div>;
};
