import React from "react";
import {Content} from "antd/es/layout/layout";
import {CodeEditor} from "./CodeEditor";
import {editor} from "monaco-editor";
import ITextModel = editor.ITextModel;
import ICodeEditorViewState = editor.ICodeEditorViewState;
import {Tabs, TabsProps} from "antd";


interface IModelState {
    model: ITextModel,
    viewstate: ICodeEditorViewState | null,
}

const pane= [{label:"aaaa", key:"aaa", children:"..file...name.."},{label:"bbb", key:"bbb"},{label:"ccc", key:"cccc"}];

export const MultiEditor: React.FC = () => {

    const renderTabBar: TabsProps['renderTabBar'] = (props, DefaultTabBar) => (
            <DefaultTabBar {...props} style={{ backgroundColor:"#fafafa" }} />
    );

    return(
        <div style={{height: "100vh", width: "100vw"}}>
            <Tabs
                id={"mutil_code_editor"}
                type="editable-card"
                tabBarStyle={{height: 25}}

                items={pane}
                renderTabBar={renderTabBar}
            />
            <CodeEditor />
        </div>
    );

}
