interface IEditorProps{
    language: string
    value: string
}
export const CodeEditor: React.FC<IStandaloneEditorConstructionOptions> = (props, context) => {
    const container = useRef<HTMLDivElement>(null);
    let editor: monaco.editor.IStandaloneCodeEditor;
    let completionItem: monaco.IDisposable;

    useEffect(() => {
        console.log("mount");
        if (container.current) {
            editor = monaco.editor.create(container.current, {
                value: props.value,
                language: props.language,
                minimap: {enabled: false}
            });
            completionItem = registerGenricSQLLanguage();
            return () => {
                console.log("dispose");
                completionItem.dispose();   //防止自动提示重复
                editor.dispose();
            };
        }
    }, []);

    return <div className="Editor" ref={container} style={{height: "100vh", width: "100vw"}}></div>;
};
